package com.gemini.leetcode.problems.array;

import java.util.HashMap;
import java.util.Map;

/**
 * https://leetcode.cn/problems/number-of-matching-subsequences/
 * 线性扫描
 *
 * @Author Gemini
 * 2022-09-01
 **/
public class NumOfMatchingSubsequences {

    public int numMatchingSubseq(String s, String[] words) {
        int count = 0;
        Map<String, Integer> map = new HashMap<>();
        boolean matched;
        for (String word : words) {
            if (map.containsKey(word)) {
                count += map.get(word);
            } else {
                matched = isMatched(s, word);
                if (matched) count++;
                map.put(word, matched ? 1 : 0);
            }
        }
        return count;
    }

    private boolean isMatched(String s, String word) {
        int m = s.length();
        int n = word.length();
        if (m < n) return false;

        int diff = m - n;
        int skipCount = 0;

        int i = 0, j = 0;
        while (i < m && j < n) {
            while (i < m && skipCount <= diff && s.charAt(i) != word.charAt(j)) {
                i++;
                skipCount++;
            }
            if (skipCount > diff) return false;
            if (i == m) return false;
            j++;
            i++;
        }

        return j == n;
    }

    public static void main(String[] args) {
        long start = System.currentTimeMillis();
        String s = "";
        String[] words = {"lv", "lw"};
        System.out.println(new NumOfMatchingSubsequences().numMatchingSubseq(s, words));
        System.out.println("time cost: " + (System.currentTimeMillis() - start) + "ms");
    }
}
